import groovy.json.JsonSlurper

def response = testRunner.testCase.testSteps["PostReq"].testRequest.response.contentAsString
def slurper = new JsonSlurper().parseText(response)

//Get product details from response
String httpStatusCode = slurper.httpStatusCode
Integer productId = slurper.body.productId
String name = slurper.body.name
String color = slurper.body.color
Integer price = slurper.body.price


//Get product details from custom proprties
String nameFromProp = testRunner.testCase.getPropertyValue("Name")
String colorFromProp = testRunner.testCase.getPropertyValue("Color")
String priceFromProp = testRunner.testCase.getPropertyValue("Price")

//Check for the product details in response
def executionStatus = ""
try {
	assert httpStatusCode == "201"
	assert name.equals(nameFromProp) : "Name should be : "+nameFromProp
	assert color.equals(colorFromProp) : "Color should be : "+colorFromProp
     assert price.toString().equals(priceFromProp) : "Price should be : "+priceFromProp 
     executionStatus = "Passed" 
     log.info("Product Id "+productId+" successfully created")
    } catch(AssertionError assertionError) {
        log.info(assertionError)
        executionStatus = "Failed"
    }

log.info("Execution Status : "+ executionStatus)
//Store Testcase name in the property file
def tcName = testRunner.testCase.name;
testRunner.testCase.getTestStepByName("PostReqProperties").setPropertyValue("TestCase Name", tcName)

//Store Request body in the property file
def req = testRunner.testCase.testSteps["PostReq"].httpRequest.requestContent;
def reqBody = new String(req)
testRunner.testCase.getTestStepByName("PostReqProperties").setPropertyValue("Request Body", reqBody)

//Store Response body in the property file
def ResBody = testRunner.testCase.testSteps["PostReq"].testRequest.response.contentAsString;
testRunner.testCase.getTestStepByName("PostReqProperties").setPropertyValue("Response Body", ResBody)

//Store httpStatusCode in the property file
testRunner.testCase.getTestStepByName("PostReqProperties").setPropertyValue("httpStatusCode", httpStatusCode)

//Store Execution status in the property file
testRunner.testCase.getTestStepByName("PostReqProperties").setPropertyValue("Execution Status", executionStatus)

//Store the Product Id at the TestSuite level
testRunner.testCase.testSuite.setPropertyValue("ProductId", productId.toString())

//Store Response data to Test Step Property File for DB testing
testRunner.testCase.getTestStepByName("PostReqProperties").setPropertyValue("ProductId", productId.toString())
testRunner.testCase.getTestStepByName("PostReqProperties").setPropertyValue("Name", name)
testRunner.testCase.getTestStepByName("PostReqProperties").setPropertyValue("Color", color)
testRunner.testCase.getTestStepByName("PostReqProperties").setPropertyValue("Price", price.toString())

import groovy.json.JsonSlurper

def response = testRunner.testCase.testSteps["GetReq"].testRequest.response.contentAsString
def slurper = new JsonSlurper().parseText(response)

//Get product details from response
String httpStatusCode = slurper.httpStatusCode
Integer productId = slurper.body.pid
String name = slurper.body.name
String color = slurper.body.color
Integer price = slurper.body.price


//Get product details from custom proprties
String nameFromProp = testRunner.testCase.testSuite.getTestCaseByName("TC_001_PostReq_AddProduct").getPropertyValue("Name")
String colorFromProp = testRunner.testCase.testSuite.getTestCaseByName("TC_001_PostReq_AddProduct").getPropertyValue("Color")
String priceFromProp = testRunner.testCase.testSuite.getTestCaseByName("TC_001_PostReq_AddProduct").getPropertyValue("Price")
String pIdFromProp = testRunner.testCase.testSuite.getPropertyValue("productId")

//Check for the product details in response
def executionStatus = ""
try {
	assert httpStatusCode == "302"
	assert productId.toString().equals(pIdFromProp) : "Product Id should be : "+pIdFromProp
	assert name.equals(nameFromProp) : "Name should be : "+nameFromProp
	assert color.equals(colorFromProp) : "Color should be : "+colorFromProp
     assert price.toString().equals(priceFromProp) : "Price should be : "+priceFromProp 
     executionStatus = "Passed" 
     log.info("Product Id "+pIdFromProp+" successfully retrieved")
    } catch(AssertionError assertionError) {
        log.info(assertionError)
        executionStatus = "Failed"
    }

log.info("Execution Status : "+ executionStatus)
//Store Testcase name in the property file
def tcName = testRunner.testCase.name;
testRunner.testCase.getTestStepByName("GetReqProperties").setPropertyValue("TestCase Name", tcName)

//Store Request body in the property file
def req = testRunner.testCase.testSteps["GetReq"].testRequest.messageExchange.rawRequestData
def reqBody = new String(req)
testRunner.testCase.getTestStepByName("GetReqProperties").setPropertyValue("Request Body", reqBody)

//Store Response body in the property file
def ResBody = testRunner.testCase.testSteps["GetReq"].testRequest.response.contentAsString;
testRunner.testCase.getTestStepByName("GetReqProperties").setPropertyValue("Response Body", ResBody)

//Store httpStatusCode in the property file
testRunner.testCase.getTestStepByName("GetReqProperties").setPropertyValue("httpStatusCode", httpStatusCode)

//Store Execution status in the property file
testRunner.testCase.getTestStepByName("GetReqProperties").setPropertyValue("Execution Status", executionStatus)

