import groovy.json.JsonSlurper

def response = testRunner.testCase.testSteps["DeleteReq"].testRequest.response.contentAsString
def slurper = new JsonSlurper().parseText(response)

//Get product details from response
String httpStatusCode = slurper.httpStatusCode
String message = slurper.message
Boolean successStatus = slurper.success

//Get product Id from custom proprties
String pIdFromProp = testRunner.testCase.testSuite.getPropertyValue("productId")

//Check for the product details in response
def executionStatus = ""
try {
	assert httpStatusCode == "200"
	assert message.equals("Product successfully removed") : "Exception while deleting product"
	assert successStatus.equals(true) : "Success Status should be True"
     executionStatus = "Passed" 
     log.info("Product Id "+pIdFromProp+" successfully removed")
    } catch(AssertionError assertionError) {
        log.info(assertionError)
        executionStatus = "Failed"
    }

log.info("Execution Status : "+ executionStatus)
//Store Testcase name in the property file
def tcName = testRunner.testCase.name;
testRunner.testCase.getTestStepByName("DelReqProperties").setPropertyValue("TestCase Name", tcName)

//Store Request body in the property file
def req = testRunner.testCase.testSteps["DeleteReq"].testRequest.messageExchange.rawRequestData
def reqBody = new String(req)
testRunner.testCase.getTestStepByName("DelReqProperties").setPropertyValue("Request Body", reqBody)

//Store Response body in the property file
def ResBody = testRunner.testCase.testSteps["DeleteReq"].testRequest.response.contentAsString;
testRunner.testCase.getTestStepByName("DelReqProperties").setPropertyValue("Response Body", ResBody)

//Store httpStatusCode in the property file
testRunner.testCase.getTestStepByName("DelReqProperties").setPropertyValue("httpStatusCode", httpStatusCode)

//Store Execution status in the property file
testRunner.testCase.getTestStepByName("DelReqProperties").setPropertyValue("Execution Status", executionStatus)
